order by
${s}

select * from (select tmp.*, rownum rn from ( SELECT sd.*, to_char(a.attendance_start_time, 'HH24:MI') startTime, to_char(a.attendance_end_time, 'HH24:MI') endTime, a.attendance_work_state, a.work_time, a.attendance_start_time from staff_department sd INNER JOIN att_record a on sd.staff_id = a.attendance_staff_id
order by
${s}
)tmp) where rn between #{rownumStart} and #{rownumEnd}
