mapper.insert:
insert into staff (
	staff_id, staff_birth,staff_phone,
	staff_address, staff_enroll_date, staff_end_date,
	staff_grade, staff_name, staff_department
)
values(
	#{staffId}, #{staffBirth}, #{staffPhone},
	#{staffAddress},#{staffEnrollDate},#{staffEndDate},
	#{staffGrade},#{staffName},#{staffDepartment}
)
