mapper.insert:
insert into staff (
	staff_id, staff_department_no, staff_birth,
	staff_tell, staff_address, staff_enroll_date,
	staff_name
)
values(
	#{staffId}, #{staffDepartmentNo},#{staffBirth},
	#{staffTell},#{staffAddress},#{staffEnrollDate},
	#{staffName}
)

mapper.update:
update staff
set
	staff_department_no=#{staffDepartmentNo}
	staff_birth=#{staffBirth}
	staff_phone=#{staffPhone}
	staff_adress=#{staffAddress}
	staff_state=#{staffState}
	staff_grade=#{staffGrade}
	staff_leave_cnt=#{staffLeaveCnt}
	staff_day_off=#{staffDayOff}
where
		staff_id=#{staffId}
