mapper.update:
update staff 
	set staff_pw = #{afterPw} 
		where staff_id = #{staffId}

update staff
		set 
			staff_phone = #{staffPhone},
			staff_address = #{staffAddress}
		where
			staff_id = #{staffId}

mapper.select:
select * from staff




	and staff_id = #{staffId}




	and instr(staff_name, #{staffName}) > 0




	and staff_department_no = #{staffDepartmentNo}
